import React ,{useEffect,useState} from 'react';
import { Link, useNavigate,useLocation,Navigate } from 'react-router-dom';
import { AppBar, Avatar, Button, Toolbar, Typography } from '@material-ui/core';
import useStyle from './style';
import decode from 'jwt-decode';
import memories from '../../images/memories.png';
import { useDispatch } from 'react-redux';

const Navbar = () => {
    const classes = useStyle();
    const [user,setUser] = useState(JSON.parse(localStorage.getItem('profile')));
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const location = useLocation();

    useEffect(()=>{
        const token = user?.token;
        if (token){
            const decodedToken = decode(token)
            if (decodedToken.exp * 1000 < new Date().getTime()) logout();
        }
        setUser(JSON.parse(localStorage.getItem('profile')));

    },[location])
    const logout = () => {
        dispatch({type:'LOGOUT'})
        navigate('/')
        window.location.reload(true);
        setUser(null)

    }

    return (
        <AppBar className={classes.appBar} position="static" color="inherit">
            <div className={classes.bransContainers}>
                <Typography component={Link} to="/" className={classes.heading} variant="h2" align="center">Memories</Typography>
                <img className={classes.image} src={memories} alt="icon" height="60" />
            </div>
            <Toolbar className={classes.Toolbar}>
                {user ? (
                    <div className={classes.profile}>
                        <Avatar className={classes.Avatar} alt={user.result.name}
                            src={user.result.imageUrl}>{user.result.name.charAt(0)}</Avatar>
                        <Typography component={Link} to='/' className={classes.userName}
                            varient='h6'>{user.result.name}</Typography>
                        <Button varient="contained" className={classes.logout}
                            color='secondary' onClick={logout}>Logout</Button>
                    </div>

                )
                    : (

                        <Button component={Link} to='/auth'
                            varient='contained' color='secondary'>Sign in</Button>
                    )
                }

            </Toolbar>
        </AppBar>
    )
}

export default Navbar
