import { initializeApp } from "firebase/app";
import { getAuth, GoogleAuthProvider, signInWithPopup } from "firebase/auth";

const firebaseConfig = {
    apiKey: "AIzaSyDyCa3AmfdHEs1I298O6PdDiHwYmTA2G0U",
    authDomain: "memories-ba6b1.firebaseapp.com",
    projectId: "memories-ba6b1",
    storageBucket: "memories-ba6b1.appspot.com",
    messagingSenderId: "419497732254",
    appId: "1:419497732254:web:4da2e89f08c5dbdf06738f"
  };

const app = initializeApp(firebaseConfig);
export const auth = getAuth(app);

const provider = new GoogleAuthProvider();

export const signInWithGoogle = () => {
  signInWithPopup(auth, provider)
    .then((result) => {
      const name = result.user.displayName;
      const email = result.user.email;
      const profilePic = result.user.photoURL;

      localStorage.setItem("name", name);
      localStorage.setItem("email", email);
      localStorage.setItem("profilePic", profilePic);
    })
    .catch((error) => {
      console.log(error);
    });
};
